MODELS_PATH = ''


def __view(request, page):
    page = str(page).replace("@", "/")
    return render(request, page + '.html')


def __all(request, model, keyvalue, page):
    _class = load_class(MODELS_PATH + '.' + model);
    datas = []
    if keyvalue == '0':
        datas = _class.objects.all()
    else:
        by = keyvalue.split("=")
        datas = _class.objects.filter(**{by[0]: by[1]}).all()

    page = str(page).replace("@", "/")
    return render(request, page + '.html', {'model': datas})


def __form(request, model, page):
    _class = load_class(MODELS_PATH + '.' + model);
    form = __make__form(_class())
    page = str(page).replace("@", "/")
    return render(request, page + '.html', {'form': form, 'model': model})


def __edit(request, model, id, page):
    _class = load_class(MODELS_PATH + '.' + model)
    obj = _class.objects.filter(pk=id).first()
    form = __make__form(obj)
    page = str(page).replace("@", "/")

    return render(request, page + '.html', {'form': form})


def __update(request, model, value, updateby):
    _class = load_class(MODELS_PATH + '.' + model);
    values = value.split("=")
    by = updateby.split("=")
    obj = _class.objects.filter(**{by[0]: by[1]}).first()
    obj.__dict__.update(**{values[0]: values[1]})
    obj.save()
    messages.success(request, "Success: Successfully edited")
    return back(request)


def __delete(request, model, id):
    _class = load_class(MODELS_PATH + '.' + model);
    obj = _class.objects.filter(pk=id).first()
    for key in _class.__annotations__:
        props = _class.__annotations__.get(key)
        if props.get('type', None) == 'file':
            folder = props.get('path', None)
            img_path = os.path.join(folder, getvalue(obj, key))
            if (os.path.exists(img_path)):
                os.remove(img_path)
    obj.delete()
    messages.success(request, "Success: Successfully deleted")
    return back(request)


def back(request):
    return HttpResponseRedirect(request.META.get('HTTP_REFERER'))


