def save(request=None):
    status = 0
    if request.method == 'POST':
        classname = request.POST.get('model', None)
        child_class = load_class(classname)
        id = request.POST.get(child_class._meta.pk.name, None)
        if str(id).strip().isdigit():
            child_classobj = child_class.objects.filter(pk=id).first()
            child = request_obj(request, child_classobj)
        else:
            child = request_obj(request, child_class)
        child_class(**child).save()

    return status


def load_class(module_name):
    components = module_name.split('.')
    mod = __import__(components[0])
    for comp in components[1:]:
        mod = getattr(mod, comp)
    return mod


def request_obj(request=None, model=None):
    kv = {}
    datas = None
    for key in model._meta.get_fields():
        props = model.__annotations__.get(key.name)
        if props is not None:
            key.name = key.name if props.get('name', None) is None else props.get('name', None)

        if request.FILES.get(key.name, False) is not False:
            file = request.FILES[key.name]
            folder = ''
            if props is not None:
                folder = props.get('path', None)
            ext = file.name.split('.')[1]
            filename = "{}.{}".format(round(time.time() * 1000),ext)
            handle_uploaded_file(file, folder, filename)
            kv[key.name] = filename
        elif props is not None and props.get('type') is 'file':
            kv[key.name] = getvalue(model, key.name)
            if kv.get(key.name) is None:
                del kv[key.name]
        else:
            if request.POST.get(key.name, None) is not None:
                value = request.POST.get(key.name)
                if value is not None and value is not '':
                    kv[key.name] = value

            if props is not None and props.get('fk', None) is not None:
                parent = props.get('model', None)
                uses = props.get('use', None)
                pobj = getattr(model, key.name, None)
                if str(getattr(model, key.name)).startswith('<django.db.models'):
                    parent_dic = request_obj(request, parent)
                    if uses is not None:
                        for __key in uses.keys():
                            parent_dic[__key] = uses.get(__key)
                    parent(**parent_dic).save()
                    kv[key.name] = parent.objects.order_by('-' + parent._meta.pk.name)[0]
                else:
                    pk_data = request_obj(request, pobj)
                    pk_data[parent._meta.pk.name] = pobj.id
                    pobj.__dict__.update(**pk_data)
                    pobj.save()
                    kv[key.name] = pobj
    return kv


def handle_uploaded_file(file, folder, filename):
    import os
    if not os.path.isdir(folder):
        os.makedirs(folder)
    with open(os.path.join(folder, filename), 'wb+') as destination:
        for chunk in file.chunks():
            destination.write(chunk)


