                f = f if props.get('name', None) is None else props.get('name', None)
                input_text = input + ' name= "' + f + '" id= "' + f + '"'
                if input is not None and props.get('type', '') is not 'fixed':
                    v = getvalue(model, f)
                    if label is not None and props.get('type', '') is not 'hidden':
                        input_text = input_text + '" placeholder= "Enter ' + label + '"'

                    if props.get('type', '') is 'combo':
                        input_text = input_text + create_combo(props.get('options'), props.get('where'), v, label)
                    elif props.get('type', '') is 'textarea':
                        input_text = input_text + '">' + v
                    elif props.get('type', '') is 'file':
                        input_text = input_text + '" value= "' + v + '">' + get_file(props, model, f, v)
                    elif v is not None:
                        input_text = input_text + '" value= "' + v + '"'
                if props.get('type', '') is not  'hidden' and props.get('avoid', None) is None:
                    template.append(input_text)
