def get_file(variables, obj, name, v):
    template = []
    if variables.get('preview', None) is not None:
        prev = variables.get('preview', None)
        template.append("<p><label for='" + name + "'><img id='img_")
        template.append(name)

        folder = variables.get('path', None) + '/' + v
        if os.path.exists(folder):
            template.append("' src='/" + __image(folder) + "'")
        else:
            template.append("' src='" + __image() + "'")
        for k, v in prev.items():
            template.append(k + "='" + str(v))
            template.append("'")
        template.append("/></label></p> <script>")
        template.append(
            "var img = document.getElementById('img_")
        template.append(name)
        template.append("');document.getElementById('")
        template.append(name)
        template.append("').onchange=function(){"
                        "if (this.files && this.files[0]) {var p = img.parentNode;"
                        "var r  = new FileReader();r.onload = function(e){"
                        "img.setAttribute('src',e.target.result);"
                        "};r.readAsDataURL(this.files[0]);"
                        "while (p.firstChild) {"
                        "p.removeChild(p.lastChild)};"
                        "p.appendChild(img);"
                        "for (var i = 1 ; i < this.files.length;i++){"
                        "var reader = new FileReader();reader.onload = function (e){"
                        "clone = img.cloneNode(true);p.appendChild(clone);"
                        "clone.removeAttribute('id');clone.style.margin='2px';"
                        "clone.setAttribute('src',e.target.result);"
                        "};reader.readAsDataURL(this.files[i]);}}")
        template.append("}")
        template.append("</script>")
    return "".join(template)

TAG_SUCCESS = 'success'
TAG_ERROR = 'error'
TAG_INFO = 'info'
TAG_WARNING = 'warning'

def addmessage(request,type,message):
    if type==TAG_SUCCESS:
        messages.success(request, message)
    elif type==TAG_ERROR:
        messages.error (request, message)
    elif type==TAG_INFO:
        messages.info (request, message)
    elif type==TAG_WARNING:
        messages.warning (request, message)
